import React from 'react'

type Props = { children: React.ReactNode }

const Layout = ({ children }: Props) => {
  return (
    <div className="border-l-[1px] border-t-[1px] pb-8 min-h-screen rounded-l-3xl border-muted-foreground/20 overflow-auto">
      {children}
    </div>
  )
}

export default Layout
